<style lang="less">    .search-condition{        display: inline-block;    }    .modal-btns{        display: inline-block;        float: right;    }</style><template>    <div class="data-search">        <div class="search-condition">            <Select v-model="model1" style="width:200px">                <Option v-for="item in cityList" :value="item.value" :key="item.value">{{ item.label }}</Option>            </Select>            <DatePicker type="daterange" :options="options2" placement="bottom-end" placeholder="Select date" style="width: 200px"></DatePicker>            <Button type="primary">搜索</Button>        </div>        <div class="modal-btns">            <Button type="primary">我的设备</Button>            <Button @click="modal2=true">分享给我的设备</Button>            <Button @click="exportData">导出excel</Button>        </div>        <Modal v-model="modal2" title="分享给我的设备" :mask-closable="false">            <div>                <div>                    <Input v-model="value"  placeholder="请输入设备名" style="width: 300px"></Input>                    <Button type="primary">搜索</Button>                </div>                <div style="margin-top: 10px;">                    <Table :columns="columns1" :data="data1"></Table>                </div>            </div>            <div slot="footer">                <Button @click="modal2=false" type="primary" >选择</Button>            </div>        </Modal>    </div></template><script>    export default{      name:'dataSearch',      data(){        return {          value:'',          modal2:false,          data1: [            {              name: 'John Brown',              age: 18,            },            {              name: 'Jim Green',              age: 24,            }          ],          columns1: [            {              type: 'selection',              width: 60,              align: 'center'            },            {              title: '账号',              key: 'name'            },            {              title: '设备参数',              key: 'age'            }          ],          cityList: [            {              value: 'New York',              label: 'New York'            },            {              value: 'New1 York',              label: 'N1ew York'            }          ],          model1: '',          options2: {            shortcuts: [              {                text: '1 week',                value () {                  const end = new Date();                  const start = new Date();                  start.setTime(start.getTime() - 3600 * 1000 * 24 * 7);                  return [start, end];                }              },              {                text: '1 month',                value () {                  const end = new Date();                  const start = new Date();                  start.setTime(start.getTime() - 3600 * 1000 * 24 * 30);                  return [start, end];                }              },              {                text: '3 months',                value () {                  const end = new Date();                  const start = new Date();                  start.setTime(start.getTime() - 3600 * 1000 * 24 * 90);                  return [start, end];                }              }            ]          }        }      },      methods:{        exportData(){          this.$refs.table.exportCsv({            filename: 'Custom data',            columns: this.columns8.filter((col, index) => index < 4),            data: this.data7.filter((data, index) => index < 4)          })        }      }    }</script>