<template>    <div class="search-list">        <div class="condition-list">            <Button type="primary" @click="chooseArea">选择区域</Button>            <Input v-model="value"  placeholder="请输入设备名" style="width: 300px"></Input>            <Button type="primary">搜索</Button>            <Button>我的设备</Button>            <Button @click="checkDevice">分享给我的设备</Button>        </div>        <Modal v-model="modal" title="区域选择" :mask-closable="false">            <div>                <Tree :data="data2" show-checkbox></Tree>            </div>            <div slot="footer">                 <Button @click="modal=false" type="primary" >确定</Button>            </div>        </Modal>        <Modal v-model="modal2" title="分享给我的设备" :mask-closable="false">            <div>                <div>                    <Input v-model="value"  placeholder="请输入设备名" style="width: 300px"></Input>                    <Button type="primary">搜索</Button>                </div>                <div style="margin-top: 10px;">                     <Table :columns="columns1" :data="data1"></Table>                </div>            </div>            <div slot="footer">                <Button @click="modal2=false" type="primary" >选择</Button>            </div>        </Modal>    </div></template><script>    export default{        name:'chartList',        data(){        return {          value:'',          modal2:false,          modal:false,          columns1: [            {              type: 'selection',              width: 60,              align: 'center'            },            {              title: '账号',              key: 'name'            },            {              title: '设备参数',              key: 'age'            }          ],          data1: [            {              name: 'John Brown',              age: 18,            },            {              name: 'Jim Green',              age: 24,            }          ],          data2:[{            title: 'parent 1',            expand: true,            children: [              {                title: 'parent 1-1',                expand: true              },              {                title: 'parent 1-2',                expand: true              }            ]          }]        }      },      methods:{        chooseArea(){          this.modal = true        },        checkDevice(){         this.modal2 = true        }      }    }</script>