<style lang="less">    .alert-recorde{        flex: 1;        display: flex;        flex-direction: column;        .device-table{            flex: 1;            display: flex;            flex-direction: column;            margin-top:10px;            .pages{                padding: 20px 0;                -webkit-box-sizing: border-box;                -moz-box-sizing: border-box;                box-sizing: border-box;                text-align: right;            }        }    }</style><template>    <div class="alert-recorde">        <data-search :recordListPage="recordListPage" @initPage = "initPage" @getList = 'getList'></data-search>        <div class="device-table">            <Table border :columns="columns" :data="alarmList"></Table>            <div class="pages">                <Page :total="totalPage"  :page-size = "15" :current="recordListPage+1" @on-change="changePage"></Page>            </div>        </div>    </div></template><script>  import dataSearch from "../components/data-search-wrapper"  export default {    name: 'recorde',    components:{      dataSearch    },    data(){      return {        recordListPage:0,        totalPage:0,        columns: [          {title: '设备名称', key: 'deviceName'},          {title: '设备SN', key: 'sn'},          {title: '报警内容', key: 'content'},          {title: '短信通知', key: 'isSms',            render: (h, params) => {              var text = '';              switch (params.row.isSms) {                case 1:                  text = '是';                  break;                case 0:                  text = '否';                  break;                default:                  text = '';                  break;              }              return h('span', text);            }          },          {title: '邮件通知', key: 'isEmail',            render: (h, params) => {              var text = '';              switch (params.row.isSms) {                case 1:                  text = '是';                  break;                case 0:                  text = '否';                  break;                default:                  text = '';                  break;              }              return h('span', text);            }          },          {title: '记录时间', key: 'date'}        ],        alarmList: []      }    },    methods:{      initPage(it){        this.recordListPage = it      },      changePage(it){        this.recordListPage = it-1      },      getList(it){        this.alarmList = it.dataList;        this.totalPage = it.count;      },    }  };</script>