<style lang="less">    .alert-recorde{        flex: 1;        display: flex;        flex-direction: column;        .device-table{            flex: 1;            display: flex;            flex-direction: column;            margin-top:10px;            .pages{                padding: 20px 0;                -webkit-box-sizing: border-box;                -moz-box-sizing: border-box;                box-sizing: border-box;                text-align: right;            }        }    }</style><template>    <div class="alert-recorde">        <data-search  :special="special" :recordListPage="recordListPage" @initPage = "initPage" @getList = 'getList'></data-search>        <div class="device-table">            <Table :highlight-row="true"  border @on-row-click="specialSelect" :columns="columns" :data="alarmList"></Table>            <div class="pages">                <Page :total="totalPage"  :page-size = "15" :current="recordListPage+1" @on-change="changePage"></Page>            </div>        </div>    </div></template><script>  import dataSearch from "../components/data-search-wrapper"  export default {    name: 'recorde',    components:{      dataSearch    },    computed: {      lang(){        if(this.$store.state.lang.is=='cn'){          this.columns = this.columns1;        }else{          this.columns = this.columns2        }        return this.$store.state.lang.is      }    },    watch:{      lang(){      }    },    data(){      return {        waringCn:[          '高温告警',          '低温告警',          '高湿告警',          '低湿告警',          '甲醛超标',          'TVOC超标',          'PM2.5超标',          'PM1.0超标',          'PM10超标',          '二氧化碳超标',          '氨气超标',          '掉电告警',          '通讯中断',          '电量不足',          '高温告警解除',          '低温告警解除',          '高湿告警解除',          '低湿告警解除',          '甲醛超标解除',          'TVOC超标解除',          'PM2.5超标解除',          'PM1.0超标解除',          'PM10超标解除',          '二氧化碳超标解