<style lang="less"></style><template>    <div class="top-search">        <Button @click="modal=true">{{lang.chooseArea}}</Button>        <Input v-model="value" :placeholder="lang.inDeviceName" style="width: 300px"></Input>        <Button type="primary">{{lang.search}}</Button>        <Button @click="modal1=true">{{lang.addService}}</Button>        <Modal v-model="modal" :title="lang.chooseArea" :mask-closable="false">            <div>                <Tree :data="data2" show-checkbox></Tree>            </div>            <div slot="footer">                <Button @click="modal=false" type="primary" >{{lang.confirm}}</Button>            </div>        </Modal>        <!--设备添加-->        <Modal v-model="modal1" :title="lang.addService" >            <div>                <Form ref="addDevice" :model="addDevice" :rules="addDeviceValidate" :label-width="150">                    <FormItem :label="lang.deviceSNnum" prop="snName">                        <Input v-model="addDevice.snName" :placeholder="lang.inSNnum"></Input>                    </FormItem>                    <FormItem :label="lang.deviceName" prop="deviceName">                        <Input v-model="addDevice.deviceName" :placeholder="lang.inDeviceName"></Input>                    </FormItem>                    <FormItem :label="lang.deviceInArea" prop="timeZone">                        <Select v-model="addDevice.timeZone">                            <Option value="beijing">New York</Option>                            <Option value="shanghai">London</Option>                            <Option value="shenzhen">Sydney</Option>                        </Select>                    </FormItem>                    <FormItem>                        <Button type="primary" size="large">{{lang.add}}</Button>                    </FormItem>                </Form>            </div>            <div slot="footer">            </div>        </Modal>    </div></template><script>    export default{      name:'topSearch',      computed: {        lang(){          return this.$store.state.lang.btns        }      },      data(){        return {          value:'',          modal:false,          modal1:false,          addDevice:{            snName:'',            deviceName:'',            timeZone:'',          },          addDeviceValidate:{            snName: [              { required: true, message: '设备SN号不能为空', trigger: 'blur' }            ],            deviceName: [              { required: true, message: '设备名称不能为空', trigger: 'blur' }            ],            timeZone: [              { required: true, message: '设备所在时区不能为空', trigger: 'change' }            ]          },          data2:[{            title: 'parent 1',            expand: true,            children: [              {                title: 'parent 1-1',                expand: true              },              {                title: 'parent 1-2',                expand: true              }            ]          }]        }      },      methods:{      }    }</script>