<style lang="less"></style><template>    <div class="top-search">        <Button @click="modal=true">选择区域</Button>        <Input v-model="value" placeholder="请输入设备名" style="width: 300px"></Input>        <Button type="primary">搜索</Button>        <Button @click="modal1=true">添加设备</Button>        <Modal v-model="modal" title="区域选择" :mask-closable="false">            <div>                <Tree :data="data2" show-checkbox></Tree>            </div>            <div slot="footer">                <Button @click="modal=false" type="primary" >确定</Button>            </div>        </Modal>        <!--设备添加-->        <Modal v-model="modal1" title="设备添加" >            <div>                <Form ref="addDevice" :model="addDevice" :rules="addDeviceValidate" :label-width="150">                    <FormItem label="设备SN号" prop="snName">                        <Input v-model="addDevice.snName" placeholder="输入设备SN号"></Input>                    </FormItem>                    <FormItem label="设备名称" prop="deviceName">                        <Input v-model="addDevice.deviceName" placeholder="输入设备名称"></Input>                    </FormItem>                    <FormItem label="设备所在时区" prop="timeZone">                        <Select v-model="addDevice.timeZone">                            <Option value="beijing">New York</Option>                            <Option value="shanghai">London</Option>                            <Option value="shenzhen">Sydney</Option>                        </Select>                    </FormItem>                    <FormItem>                        <Button type="primary" size="large">添加</Button>                    </FormItem>                </Form>            </div>            <div slot="footer">            </div>        </Modal>    </div></template><script>    export default{      name:'topSearch',      data(){        return {          value:'',          modal:false,          modal1:false,          addDevice:{            snName:'',            deviceName:'',            timeZone:'',          },          addDeviceValidate:{            snName: [              { required: true, message: '设备SN号不能为空', trigger: 'blur' }            ],            deviceName: [              { required: true, message: '设备名称不能为空', trigger: 'blur' }            ],            timeZone: [              { required: true, message: '设备所在时区不能为空', trigger: 'change' }            ]          },          data2:[{            title: 'parent 1',            expand: true,            children: [              {                title: 'parent 1-1',                expand: true              },              {                title: 'parent 1-2',                expand: true              }            ]          }]        }      },      methods:{      }    }</script>