<style lang="less">    .device-list-table{        padding: 10px 0;        box-sizing: border-box;        .pages{            padding: 20px 0;            box-sizing: border-box;            text-align: right;        }    }    .inline-it{        display: inline-block;    }    .input-width{        width: 120px!important;        display: inline-block;        .ivu-input-wrapper{            width: 120px;        }    }</style><template>    <div class="device-add">        <top-search></top-search>        <div class="device-list-table">            <Table :columns="columns" :data="data1"></Table>            <div class="pages">                <Page :total="100" show-elevator></Page>            </div>        </div>        <!--设备分享-->        <Modal v-model="modal3" title="111" @on-ok="ok" @on-cancel="cancel">            <Form ref="shareDevice" :model="shareDevice" :rules="addDeviceValidate" :label-width="100">                <FormItem label="账号" prop="snName">                    <Input v-model="shareDevice.account" placeholder="输入设备名称"></Input>                </FormItem>            </Form>            <div slot="footer">                <Button type="primary" size="large"  @click="del">保存</Button>            </div>        </Modal>        <!--设备编辑-->        <Modal v-model="modal2" title="设备编辑" @on-ok="ok" @on-cancel="cancel">            <div>                <Form ref="editDevice" :model="editDevice" :rules="addDeviceValidate" :label-width="100">                    <FormItem label="设备SN号" prop="snName">                       <span>qrqwtqtwqt</span>                    </FormItem>                    <FormItem label="设备名称" prop="deviceName">                        <Input v-model="editDevice.deviceName" placeholder="输入设备名称"></Input>                    </FormItem>                    <FormItem label="设备所在时区" prop="timeZone">                        <Select v-model="editDevice.timeZone" placeholder="选择所在时区">                            <Option value="beijing">New York</Option>                            <Option value="shanghai">London</Option>                            <Option value="shenzhen">Sydney</Option>                        </Select>                    </FormItem>                    <FormItem label="温度预警范围" prop="deviceName">                        <Input v-model="editDevice.deviceName" placeholder="输入湿度预警值" class="input-width"></Input>                        <span>-</span>                        <Input v-model="editDevice.deviceName" placeholder="输入湿度预警值" class="input-width"></Input>                    </FormItem>                    <FormItem label="湿度预警范围" prop="deviceName">                        <Input v-model="editDevice.deviceName" placeholder="输入湿度预警值" class="input-width"></Input>                        <span>-</span>                        <Input v-model="editDevice.deviceName" placeholder="输入湿度预警值" class="input-width"></Input>                    </FormItem>                    <FormItem label="上报时间间隔" prop="timeZone" class="inline-it">                        <Select v-model="editDevice.timeZone" placeholder="选择时间间隔">                            <Option value="beijing">New York</Option>                            <Option value="shanghai">London</Option>                            <Option value="shenzhen">Sydney</Option>                        </Select>                    </FormItem>                    <FormItem label="记录时间间隔" prop="timeZone" class="inline-it">                        <Select v-model="editDevice.timeZone" placeholder="选择时间间隔">                            <Option value="beijing">New York</Option>                            <Option value="shanghai">London</Option>                            <Option value="shenzhen">Sydney</Option>                        </Select>                    </FormItem>                    <FormItem label="断电报警" prop="deviceName">                        <i-switch v-model="editDevice.switch" size="large">                            <span slot="open">On</span>                            <span slot="close">Off</span>                        </i-switch>                    </FormItem>                    <FormItem label="短信通知" prop="deviceName" >                        <i-switch v-model="editDevice.switch" size="large">                            <span slot="open">On</span>                            <span slot="close">Off</span>                        </i-switch>                        <div>                            <span>联系人</span>                            <Input v-model="value" size="small" placeholder="输入联系人" style="width: 100px"></Input>                            <span>手机号码</span>                            <Input v-model="value" size="small" placeholder="输入手机号码" style="width: 100px"></Input>                        </div>                        <Button size="small"><Icon type="plus"></Icon></Button>                    </FormItem>                    <FormItem label="邮件通知" prop="deviceName">                        <i-switch v-model="editDevice.switch" size="large">                            <span slot="open">On</span>                            <span slot="close">Off</span>                        </i-switch>                        <div>                            <span>联系人</span>                            <Input v-model="value" size="small" placeholder="输入联系人"  style="width: 100px"></Input>                            <span>邮箱</span>                            <Input v-model="value" size="small" placeholder="输入邮箱" style="width: 100px"></Input>                        </div>                        <Button size="small"><Icon type="plus"></Icon></Button>                    </FormItem>                </Form>            </div>            <div slot="footer">                <Button type="primary" size="large" long @click="del">保存</Button>            </div>        </Modal>    </div></template><script>    import topSearch from "./components/top-search"  export default {    name: 'deviceAdd',    components:{      topSearch    },    computed: {      lang(){        return this.$store.state.lang.is      },      langContent(){        console.log( this.$store.state.lang.bts)        return this.$store.state.lang.bts      }    },    watch:{      lang(newVal,oldVal){        if(newVal=='cn'){          this.columns = this.columns1        }else{          this.columns = this.columns2        }      }    },    beforeMount(){      if(this.$store.state.lang.is=='cn'){        this.columns = this.columns1      }else {        this.columns = this.columns2      }    },    data(){      return {        columns:'',        value:'',        modal2:false,        modal3:false,        shareDevice:{          account:''        },        editDevice:{          snName:'',          deviceName:'',          timeZone:'',          switch: true        },        addDeviceValidate:{          snName: [            { required: true, message: '设备SN号不能为空', trigger: 'blur' }          ],          deviceName: [            { required: true, message: '设备名称不能为空', trigger: 'blur' }          ],          timeZone: [            { required: true, message: '设备所在时区不能为空', trigger: 'change' }          ],          account:[            { required: true, message: '账号不能为空', trigger: 'blur' }          ]        },        modal1:false,        columns2:[        {title: 'device-SN', key: 'name'},        {title: 'device-name', key: 'age'},        {title: 'temperature-range（C）', key: 'age'},        {title: 'humidty-range（%rh）', key: 'age'},        {title: 'report-gap（min）', key: 'age'},        {title: 'recorde-gap（min）', key: 'age'},        {title: 'own-area', key: 'age'},        {title: 'power-outage-alarm', key: 'age'},        {title: 'short-note', key: 'age'},        {title: 'email-note', key: 'age'},        {title: 'state', key: 'age'},        {title: 'operate', key: 'action', fixed: 'right', width: 160,          render: (h, params) => {            return h('div', [              h('Button', {                props: {type: 'text', size: 'small'},                on: {                  click: () => {                    this.edit(params.index)                  }                }              }, 'edit'),              h('Button', {                props: {type: 'text', size: 'small'},                on: {                  click: () => {                    this.delet(params.index)                  }                }              }, 'delet'),              h('Button', {                props: {type: 'text', size: 'small'},                on: {                  click: () => {                    this.share(params.index)                  }                }              }, 'share')            ]);          }        }      ],        columns1: [          {title: '设备SN号', key: 'name'},          {title: '设备名称', key: 'age'},          {title: '温度预警范围（C）', key: 'age'},          {title: '温度预警范围（%rh）', key: 'age'},          {title: '上报时间间隔（分钟）', key: 'age'},          {title: '记录间隔（分钟）', key: 'age'},          {title: '所属区域', key: 'age'},          {title: '断电报警', key: 'age'},          {title: '短信通知', key: 'age'},          {title: '邮件通知', key: 'age'},          {title: '状态', key: 'age'},          {title: '操作', key: 'action', fixed: 'right', width: 150,            render: (h, params) => {              return h('div', [                h('Button', {                  props: {type: 'text', size: 'small'},                  on: {                    click: () => {                      this.edit(params.index)                    }                  }                  }, '编辑'),                h('Button', {                  props: {type: 'text', size: 'small'},                  on: {                    click: () => {                      this.delet(params.index)                    }                  }                }, '删除'),                h('Button', {                  props: {type: 'text', size: 'small'},                  on: {                    click: () => {                      this.share(params.index)                    }                  }                }, '分享')              ]);            }          }        ],        data1: [          {            name: 'John Brown',            age: 18,          },          {            name: 'Jim Green',            age: 24,          },          {            name: 'Joe Black',            age: 30,          },          {            name: 'Jon Snow',            age: 26,          }        ]      }    },    methods:{      ok(){},      cancel(){},      del(){},      add(it){        this.modal1 = true;        console.log(it)      },      edit(it){        this.modal2 = true;        console.log(it)      },      delet(it){        console.log(it)        this.$Modal.warning({          title: "删除设备",          content: "确定要删除该设备吗？"        });      },      share(it){        this.modal3 = true;        console.log(it)      }    }  };</script>