<style lang="less">    .table-list{        padding:20px 0;        box-sizing: border-box;    }    .pages{        padding: 20px 0;        box-sizing: border-box;        text-align: right;    }</style><template>    <div class="device-share">       <div>           <Input v-model="value" placeholder="输入分享的账号" style="width: 300px"></Input>           <Button type="primary">搜索</Button>       </div>        <div class="table-list">            <Table :columns="columns1" :data="data1"></Table>        </div>        <div class="pages">            <Page :total="100" show-elevator></Page>        </div>        <Modal v-model="modal1" title="我分享的设备">            <div>                <div>                    <Input v-model="value" placeholder="输入设备名称" style="width: 300px"></Input>                    <Button type="primary">搜索</Button>                </div>                <div  class="table-list">                    <Table :columns="columns2" :data="data2"></Table>                    <div class="pages">                        <Page :total="100" show-elevator></Page>                    </div>                </div>            </div>            <div slot="footer"></div>        </Modal>        <Modal title="删除设备" v-model="modal2"  :mask-closable="true">            <p>确认删除该设备？</p>        </Modal>    </div></template><script>  export default {    name: 'deviceShare',    data(){      return{        value:'',        modal1:false,        modal2:false,        columns1: [          {title: '分享的账号', key: 'shareAccount'},          {title: '分享的设备总数', key: 'shareDeviceNum'},          {title: '操作', key: 'action', fixed: 'right', width: 120,            render: (h, params) => {              return h('div', [                h('Button', {                  props: {type: 'text', size: 'small'},                  on:{                    click: () => {                      this.checkIt(params.index)                    }                  }                }, '查看'),                h('Button', {                  props: {type: 'text', size: 'small'},                  on:{                    click: () => {                      this.deleIt(params.index)                    }                  }                }, '删除')              ]);            }          }        ],        data1: [          {            shareAccount: 'John Brown',            shareDeviceNum: 18,          }        ],        columns2: [          {            title: 'Name',            key: 'name'          },          {            title: 'Age',            key: 'age'          },          {title: '操作', key: 'action', fixed: 'right', width: 120,            render: (h, params) => {              return h('div', [                h('Button', {                  props: {type: 'text', size: 'small'}                }, '删除')              ]);            }          }        ],        data2: [          {            name: 'John Brown',            age: 18          }        ]      }    },    methods:{      checkIt(it){        console.log(it);        this.modal1 = true;      },      deleIt(it){        console.log(it);        this.modal2 = true;      }    }  };</script>