<template>    <div class="chart-show">        <search-wrapper></search-wrapper>        <div class="device-wrapper">            <ul class="device-list">                <li>                    <div class="device-one" id="chart01">                    </div>                    <div class="device-one" id="chart02">                    </div>                </li>                <li>                    <div class="device-one" id="chart03">                    </div>                    <div class="device-one" id="chart04">                    </div>                </li>                <li>                    <div class="device-one" id="chart05">                    </div>                    <div class="device-one" id="chart06">                    </div>                </li>                <li>                    <div class="device-one" id="chart07">                    </div>                    <div class="device-one" id="chart08">                    </div>                </li>                <li>                    <div class="device-one" id="chart09">                    </div>                    <div class="device-one" id="chart10">                    </div>                </li>                <li>                    <div class="device-one" id="chart11">                    </div>                    <div class="device-one" id="chart12">                    </div>                </li>                <li>                    <div class="device-one" id="chart13">                    </div>                    <div class="device-one" id="chart14">                    </div>                </li>            </ul>        </div>    </div></template><style lang="less">    @import './online-data.less';</style><script>  import searchWrapper from '../../components/search-wrapper.vue'    export default {      name:'chartShow',      data(){        return {          value:'',          chartList:['chart01','chart02','chart03','chart04','chart05',            'chart06','chart07','chart08','chart09','chart10','chart11',            'chart12','chart13','chart14']        }      },      components:{        searchWrapper      },      mounted(){        const _this  = this;        this.drawChart(_this.chartList)      },      methods:{        drawChart(id){          const _this = this;          let options = {            title:{              text: '仪表盘',              textStyle:{                fontSize:12,                align:'center'              }            },            tooltip: {              formatter: "{a} <br/>{b} : {c}%"            },            series: [{              name: '速度',              type: 'gauge',              center: ['50%', '75%'],              max:280,              radius:'100%',  // 半径              startAngle:180,  //起始位置              endAngle:0,   //终点位置              detail: {                formatter: '{value}Km/h',                textStyle: {                  fontSize: 12                },                offsetCenter:['100%','-114%']              },              splitLine: {           // 分隔线                length: 5,         // 属性length控制线长                lineStyle: {       // 属性lineStyle（详见lineStyle）控制线条样式                  color: 'auto'                }              },              axisLine: {            // 坐标轴线                lineStyle: {       // 属性lineStyle控制线条样式                  width: 10                }              },              pointer:{                length:'80%', //指针                width:4              },              data: [{    //当前值                value: 100,                name: '完成率'              }]            }]          };          this.chartList.forEach(function(val,index){            let id = document.getElementById(val)            let chart = _this.$echarts.init(id);            chart.setOption(options);          })        }      }    }</script>