<style lang="less" scoped>    .map-show{        width: 100%;        height: 100%;        #allmap{            width: 100%;            height: 100%;            margin-top: 20px;        }    }</style><template>    <div class="map-show">        <div>            <Select                    clearable                    style="width:200px"                    placeholder="输入设备名称"                    v-model="choosedSn"                    @on-change="selectDevice"                    filterable                    remote                    :remote-method="remoteMethod"                    :loading="loading">                <Option v-for="(option, index) in deviceList" :value="option.sn" :key="index">{{option.deviceName}}</Option>            </Select>            <!--<Input v-model="sn" placeholder="输入设备SN号" style="width: 300px" />-->            <Button type="primary" @click="getDeviceGps">搜索</Button>        </div>        <div id="allmap"></div>    </div></template><script>  import  start from "../../../../styles/start.png";  import  end from "../../../../styles/end.png"    export default{      name:'mapIn',      data(){        return {          deviceList:[],          loading:false,          deviceName:'',          choosedSn:'',          map:'',          opts: {            width : 200,     // 信息窗口宽度            height: 120,     // 信息窗口高度            title : "信息" , // 信息窗口标题            enableMessage:true//设置允许信息窗发送短息          }        }      },      mounted(){        this.map = new BMap.Map("allmap");    // 创建Map实例        this.map.enableScrollWheelZoom(true);     //开启鼠标滚轮缩放        //添加地图类型控件        this.map.addControl(new BMap.MapTypeControl({          mapTypes:[            BMAP_NORMAL_MAP,            BMAP_HYBRID_MAP          ]}));        this.initGap();        this.remoteMethod()      },      methods:{          selectDevice(it){            this.choosedSn = it          },        remoteMethod(){          this.deviceList = [];          this.loading = true;          let userId = this.$cookie.get('userId');          let accessToken = this.$cookie.get('accessToken');          let permissions = this.$cookie.get('permissions');          let data = {            sharedUserId:null,            deviceName:'',            userId:userId,            loginType:2,            accessToken:accessToken,            permissions:permissions          };          this.$http({            method:'post',            url:this.$util.ajaxUrl+"/warning/getDevices",            data          },(res)=>{            let result = res.data;            if(result.code==0){              this.deviceList = [...result.data];              this.loading = false;            }else{              this.$Message.error(res.data.msg)            }          },(erro)=>{            console.log(erro);          })        },        getDeviceGps(){          let userId = this.$cookie.get('userId');          let accessToken = this.$cookie.get('accessToken');          let permissions = this.$cookie.get('permissions');          let data = {            sn:this.choosedSn,            userId:userId,            loginType:2,            accessToken:accessToken,            permissions:permissions          };          this.$http({            method:'post',            url:this.$util.ajaxUrl+"/map/getDeviceGps",            dataType: "json",            contentType: 'application/json;charset=UTF-8',            data          },(res)=>{            let result = res.data;            if(result.code==0){              var myArray = result.data.dataList;              var pointArray = new Array();              for (var i = 0; i < myArray.length; i ++) {                var point = new BMap.Point(myArray[i].longitude,myArray[i].latitude);// 创建点坐标                pointArray[i]=point;                var marker = new BMap.Marker(point);// 创建标注                if(i==0){                  var myIcon1 = new BMap.Icon(start, new BMap.Size(40,80));// 自定义标注图                  marker = new BMap.Marker(point,{icon:myIcon1});                  this.map.addOverlay(marker);//添加起点                }                if(i==(myArray.length-1)){                  var myIcon2 = new BMap.Icon(end, new BMap.Size(40,80));// 自定义标注图                  marker = new BMap.Marker(point,{icon:myIcon2});                  this.map.addOverlay(marker);//添加终点                }              }              var point = new BMap.Point(myArray[0]==null?116.404:myArray[0].longitude, myArray[0]==null?39.915:myArray[0].latitude);//兼容空值报错 默认北京              var polyline = new BMap.Polyline(pointArray,                {strokeColor:"blue",//设置颜色                  strokeWeight:8, //宽度                  strokeOpacity:0.5//透明度                }              )              this.map.addOverlay(polyline);              this.map.centerAndZoom(point, 15);  // 初始化地图,设置中心点坐标和地图级别              this.map.setViewport(pointArray); //让所有点可见            }else{              this.$Message.error(res.data.msg)            }          },(erro)=>{            console.log(erro);          })        },        addClickHandler(myArraydata,marker){//提示窗口          const _this = this;//          marker.addEventListener("click",function(e){//              _this.openInfo("设备名称："+myArraydata.deviceName+"<br> SN号:"+myArraydata.sn,e);//              _this.choosedSn = myArraydata.sn//            });          marker.addEventListener("click",function(e){            _this.openInfo("设备名称："+myArraydata.deviceName+"<br> SN号:"+myArraydata.sn                +"<br> 温度:"+myArraydata.temperature +" ℃ "                +( myArraydata.humidity==0||myArraydata.humidity==null?"":"<br> 湿度:"+myArraydata.humidity+" %RH ")                ,e);             _this.choosedSn = myArraydata.sn            }          );        },        openInfo(content,e){//提示窗口          const _this = this;          var p = e.target;          var point = new BMap.Point(p.getPosition().lng, p.getPosition().lat);          var infoWindow = new BMap.InfoWindow(content,_this.opts);  // 创建信息窗口对象          this.map.openInfoWindow(infoWindow,point); //开启信息窗口        },        initGap(){          let userId = this.$cookie.get('userId');          let accessToken = this.$cookie.get('accessToken');          let permissions = this.$cookie.get('permissions');          let data = {            userId:userId,            loginType:2,            accessToken:accessToken,            permissions:permissions          };          this.$http({            method:'post',            url:this.$util.ajaxUrl+"/map/getGps",            dataType: "json",            contentType: 'application/json;charset=UTF-8',            data          },(res)=>{            let result = res.data;            if(result.code==0){              var myArray = result.data.dataList;              var pointArray = new Array();              for (var i = 0; i < myArray.length; i ++) {                var point = new BMap.Point(myArray[i].longitude,myArray[i].latitude);// 创建点坐标                pointArray[i]=point;                var marker = new BMap.Marker(point); // 创建标注                this.map.addOverlay(marker); // 将标注添加到地图中                this.addClickHandler(myArray[i],marker);  // 添加标注点击事件              };              var point = new BMap.Point(myArray[0]==null?116.404:myArray[0].longitude, myArray[0]==null?39.915:myArray[0].latitude);//兼容空值报错 默认北京              this.map.centerAndZoom(point, 15);  //我加了让所有点可见后  这个好像没用     初始化地图,设置中心点坐标和地图级别              this.map.setViewport(pointArray); //让所有点可见            }else{              this.$Message.error(res.data.msg)            }          },(erro)=>{            console.log(erro);          })        }      }    }</script>