<style lang="less" scoped>    .online-data{      flex:1;        display: flex;        flex-direction: column;        .ivu-tabs{            flex:1;            display: flex;            flex-direction: column;            .ivu-tabs-content{                flex:1;            }        }    }</style><template>    <div class="online-data">        <Tabs :value="theName" @on-click="changeTab">            <TabPane :label="lang.title[0]" name="name1">                <data-show v-if="theName=='name1'" :theName="theName"></data-show>            </TabPane>            <TabPane :label="lang.title[1]" name="name2">                <chart-show  v-if="theName=='name2'" :theName="theName"></chart-show>            </TabPane>            <TabPane :label="lang.title[2]" name="name3">                <map-show v-if="theName=='name3'"></map-show>            </TabPane>        </Tabs>    </div></template><script>  import dataShow from './components/data-show.vue'  import chartShow from './components/chart-show.vue'  import mapShow from './components/map-show.vue'  export default {    name: 'online_data',    computed: {      lang(){        return this.$store.state.lang.onLine      }    },    data(){      return {        value:'',        theName:'name1'      }    },    components:{      dataShow,      chartShow,      mapShow    },    methods:{      changeTab(it){        this.theName = it      }    }  };</script>