<style lang="less" scoped>    @import "area-management";</style><template>    <div class="area-management">        <div class="contrl-wrapper">            <div class="contrl-left">                <div class="contrl-left-title">区域</div>                <div class="the-tree">                    <Tree :data="data2" show-checkbox></Tree>                </div>                <div class="btns-wrapper">                    <span class="btn"><Icon type="plus-round"></Icon></span>                    <span class="btn"><Icon type="minus-round"></Icon></span>                </div>            </div>            <div class="contrl-right">                <div class="contrl-right-title">详情</div>                <div class="the-detail">                    <Form ref="formValidate" :model="formValidate" :rules="ruleValidate" :label-width="80">                        <FormItem label="区域名称" prop="name">                            <Input v-model="formValidate.name" placeholder="请输入区域名称"></Input>                        </FormItem>                        <FormItem label="区域描述" prop="value">                            <Input v-model="formValidate.value" type="textarea" placeholder="请输入对区域的描述"></Input>                        </FormItem>                        <FormItem label="设备分配" prop="value">                            <Transfer                                    :data="data3"                                    :target-keys="targetKeys2"                                    filterable                                    :filter-method="filterMethod"                                    @on-change="handleChange2"></Transfer>                        </FormItem>                    </Form>                </div>                <div class="save-all">                    <Button type="primary" size="small" >保存</Button>                </div>            </div>        </div>    </div></template><script>  export default {    name: 'areaManagement',    methods:{      getMockData () {        let mockData = [];        for (let i = 1; i <= 20; i++) {          mockData.push({            key: i.toString(),            label: 'Content ' + i,            description: 'The desc of content  ' + i,            disabled: Math.random() * 3 < 1          });        }        return mockData;      },      getTargetKeys () {        return this.getMockData()          .filter(() => Math.random() * 2 > 1)          .map(item => item.key);      },      handleChange2 (newTargetKeys) {        this.targetKeys2 = newTargetKeys;      },      filterMethod (data, query) {        return data.label.indexOf(query) > -1;      }    },    data(){      return {        data3: this.getMockData(),        targetKeys2: this.getTargetKeys(),        formValidate:{          name:'',          value:''        },        ruleValidate:{          name: [            { required: true, message: '区域名称不能为空', trigger: 'blur' }          ],          value: [            { required: true, message: '区域描述不能为空', trigger: 'blur' }          ]        },        data2: [          {            title: 'parent 1',            expand: true,            children: [              {                title: 'parent 1-1',                expand: true              },              {                title: 'parent 1-2',                expand: true              }            ]          }        ]      }    }  };</script>