<style lang="less">    .info-list{        li{            min-height: 40px;            height: auto;            display: flex;            span{                display: inline-block;            }            span:first-child{                width: 80px;            }            span:last-child{                flex: 1;            }            .charge-btn{                text-align: center;                padding: 30px 0;                .ivu-btn{                    width: 200px;                }            }        }    }</style><template>    <div class="user-info">        <Card style="width:350px">            <ul class="info-list">                <li>                    <span>账号：</span>                    <span>1111</span>                </li>                <li>                    <span>公司名称：</span>                    <span>                        <Input v-model="value" placeholder="请输入公司名称." style="width: 200px"></Input>                    </span>                </li>                <li>                    <span>公司地址：</span>                    <span>                        <Input v-model="value" placeholder="请输入公司地址" style="width:200px"></Input>                    </span>                </li>                <li>                    <span>手机号码：</span>                    <span>123124142124</span>                    <span>                         <Button type="text" size="small" @click="modal1=true">更改</Button>                    </span>                </li>                <li>                    <span class="charge-btn">                        <Button type="primary" size="small" long>保存</Button>                    </span>                </li>            </ul>        </Card>        <Modal v-model="modal1" title="更改手机号">            <div>                <Form ref="formValidate" :model="formValidate" :rules="ruleValidate" :label-width="80">                    <FormItem label="手机号" prop="name">                        <Input v-model="formValidate.name" placeholder="Enter your name"></Input>                    </FormItem>                    <FormItem label="验证码" prop="mail">                        <Input v-model="formValidate.mail" placeholder="Enter your e-mail">                            <span slot="append"> <Button type="primary" size="small" >获取验证码</Button></span>                        </Input>                    </FormItem>                </Form>            </div>            <div slot="footer">                <Button type="primary">确认</Button>                <Button >取消</Button>            </div>        </Modal>    </div></template><script>  export default {    name: 'userInfo',    data(){      return {        value:'',        modal1:false,        formValidate:{          name:'',          mail:''        },        ruleValidate:{          name: [            { required: true, message: '请输入正确的手机号', trigger: 'blur' }          ],          mail: [            { required: true, message: '请输入正确的验证码', trigger: 'blur' }          ]        }      }    }  };</script>