<style lang="less">    .user-table{        margin-top: 20px;    }</style><template>    <div class="user-share">        <div>            <Button type="primary" @click="modal1=true">添加</Button>        </div>        <div class="user-table">            <Table border ref="selection" :columns="columns4" :data="data1"></Table>        </div>        <!--添加账号-->        <Modal                v-model="modal1"                title="添加账号">           <div>               <Form ref="formInline" :model="formInline" :rules="ruleInline"  :label-width="80">                   <FormItem label="用户名" prop="user">                       <Input type="text" v-model="formInline.user" placeholder="输入用户名"></Input>                   </FormItem>                   <FormItem label="说明" prop="password">                       <Input type="text" v-model="formInline.password" placeholder="输入账号说明"></Input>                   </FormItem>               </Form>           </div>            <div slot="footer">                <Button type="primary">确定</Button>            </div>        </Modal>        <Modal v-model="modal2" title="分配设备">            <div>                <div>                    <Button >选择区域</Button>                    <Input v-model="value" placeholder="输入设备名称" style="width: 300px"></Input>                    <Button type="primary">搜索</Button>                </div>                <div  class="table-list">                    <Table :columns="columns2" :data="data2"></Table>                    <div class="pages">                        <Page :total="100" show-elevator></Page>                    </div>                </div>            </div>            <div slot="footer"></div>        </Modal>        <Modal title="删除设备" v-model="modal3"  :mask-closable="true">            <p>确认删除该设备？</p>            <div slot="footer">                <Button type="primary">确认</Button>            </div>        </Modal>        <Modal title="重置密码" v-model="modal4"  :mask-closable="true">            <p>确认重置该设备密码？</p>            <div slot="footer">                <Button type="primary">确认</Button>            </div>        </Modal>    </div></template><script>  export default {    name: 'userShare',    data(){      return {        value:'',        data2: [          {            name: 'John Brown',            age: 18,            qq:11          }        ],        columns2: [          {type: 'selection', width: 60, align: 'center'},          {            title: '设备名称',            key: 'name'          },          {            title: '设备SN号',            key: 'age'          },          {            title: '所属区域',            key: 'qq'          }        ],        formInline:{          user:'',          password:''        },        ruleInline:{          user:[            { required: true, message: '用户名不能为空', trigger: 'blur' }            ],          password:[            { required: true, message: '用户说明不能为空', trigger: 'blur' }          ]        },        modal1:false,        modal2:false,        modal3:false,        modal4:false,        columns4: [          {type: 'selection', width: 60, align: 'center'},          {title: 'Name', key: 'name'},          {title: 'Age', key: 'age'},          {title: '操作', key: 'action', fixed: 'right', width: 150,            render: (h, params) => {              return h('div', [                h('Button', {                  props: {type: 'text', size: 'small'},                  on: {                    click: () => {                      this.resetPsd(params.index)                    }                  }                }, '重置密码'),                h('Button', {                  props: {type: 'text', size: 'small'},                  on: {                    click: () => {                      this.delet(params.index)                    }                  }                }, '删除'),                h('Button', {                  props: {type: 'text', size: 'small'},                  on: {                    click: () => {                      this.shareDevice(params.index)                    }                  }                }, '分配设备')              ]);            }          }        ],        data1: [          {            name: 'Joe Black',            age: 30,            address: 'Sydney No. 1 Lake Park',            date: '2016-10-02'          },          {            name: 'Jon Snow',            age: 26,            address: 'Ottawa No. 2 Lake Park',            date: '2016-10-04'          }        ]      }    },    methods:{      resetPsd(){        this.modal4 = true;      },      shareDevice(){        this.modal2 = true;      },      delet(){        this.modal3 = true;      },      ok(){},      cancel(){}    }  };</script>