<style lang="less">    @import './online-data.less';</style><template>    <div class="device-data">        <div class="condition-list">            <Button type="primary" @click="modal=true">选择区域</Button>            <Input v-model="value"  placeholder="请输入设备名" style="width: 300px"></Input>            <Button type="primary" >搜索</Button>            <Button>我的设备</Button>            <Button @click="modal2=true">分享给我的设备</Button>        </div>        <div class="device-wrapper">            <Card class="card-style">                <p slot="title">                    <Icon type="ios-film-outline"></Icon>                    设备1                </p>                <div>                    <span>1324</span>                </div>                <div class="switch-change">                    <span>继电器1</span>                     <i-switch></i-switch>                    <span>继电器1</span>                    <i-switch></i-switch>                </div>            </Card>            <Card class="card-style">                <p slot="title">                    <Icon type="ios-film-outline"></Icon>                    设备1                </p>                <div>                    <span>1324</span>                </div>                <div class="switch-change">                    <span>继电器1</span>                    <i-switch></i-switch>                    <span>继电器1</span>                    <i-switch></i-switch>                </div>            </Card>        </div>        <Modal v-model="modal" title="区域选择" :mask-closable="false">            <div>                <Tree :data="data2" show-checkbox></Tree>            </div>            <div slot="footer">                <Button @click="modal=false" type="primary" >确定</Button>            </div>        </Modal>        <Modal v-model="modal2" title="分享给我的设备" :mask-closable="false">            <div>                <div>                    <Input v-model="value"  placeholder="请输入设备名" style="width: 300px"></Input>                    <Button type="primary">搜索</Button>                </div>                <div style="margin-top: 10px;">                    <Table :columns="columns1" :data="data1"></Table>                </div>            </div>            <div slot="footer">                <Button @click="modal=false" type="primary" >选择</Button>            </div>        </Modal>    </div></template><script>    export default {      name:"deviceData",      data(){        return{          switch1:true,          value:'',          modal2:false,          columns1: [            {              type: 'selection',              width: 60,              align: 'center'            },            {              title: '账号',              key: 'name'            },            {              title: '设备参数',              key: 'age'            }          ],          data1: [            {              name: 'John Brown',              age: 18,            },            {              name: 'Jim Green',              age: 24,            }          ],          modal:false,          data2:[{              title: 'parent 1',              expand: true,              children: [                {                  title: 'parent 1-1',                  expand: true                },                {                  title: 'parent 1-2',                  expand: true                }              ]            }]        }      }    }</script>